import React from 'react'
import './Update.css'
import {useState, useEffect} from "react"
import axios from "axios"



function Update(props) {
  const {_id,name,description,category,price,imageUrl} = props.obj


  const [names,setNames]= useState("")
  const [descriptions,setDescriptions]= useState("")
  const [prices,setPrices]= useState("")
  const [imageUrls,setImageUrls]= useState("")
  const [categories,setCategories] = useState("")
  
  useEffect(()=>{
  setNames(name)
  setDescriptions(description)
  setPrices(price)
  setImageUrls(imageUrl)
  setCategories(category)
  
  },[])
  
  const handleClick = () => {
    axios.put(`http://localhost:5000/api/products/${_id}`,{
   name:names,
  description:descriptions,
  price:prices,
  imageUrl:imageUrls,
  category:categories
  }).then((result)=>{
  console.log(result)
    }).catch(err=>console.log(err))
  
  return ( 
<form action="" class="container">
  <div class="input-container">
      <div class="input-content">
          <div class="input-dist">
              <div class="input-type">
                  <input placeholder="Name" required
                     id="outlined-required"
                     label="name"
                     defaultValue={name}
                     onChange={(e)=>setNames(e.target.value)}
                     />  
                  <input placeholder="Description" required="" type="text" class="input-is"/>
                  <input placeholder="Categories" required="" type="text" class="input-is"/>  
                  <input placeholder="Image Url" required="" type="text" class="input-is"/>  
              </div>
          </div>
      </div>
      <button>
Submit
</button>
  </div>
  
</form>
)} }

export default Update
