import React from 'react'
import './Update.css'
import {useState, useEffect} from "react"
import axios from "axios"
import { useLocation } from 'react-router-dom'




function Update(props) {
    console.log(props.up);
  const {_id,name,description,categories,imageUrl} = props.update


  const [names,setNames]= useState("")
  const [descriptions,setDescriptions]= useState("")
  const [imageUrls,setImageUrls]= useState("")
  const [categoriess,setCategories] = useState("")
  const location = useLocation()

  useEffect(()=>{
  setNames(name)
  setDescriptions(description)
  setImageUrls(imageUrl)
  setCategories(categories)
  
  },[])
  
  const handleClick = () => {
    axios.put(`http://localhost:5000/api/work/${_id}`,{
   name:name,
  description:description,
  imageUrl:imageUrl,
  categories:categories
  }).then((result)=>{
  console.log(result)
    }).catch(err=>console.log(err))
  
  return ( 
<form action="" class="container">
  <div class="input-container">
      <div class="input-content">
          <div class="input-dist">
              <div class="input-type">
                  <input placeholder="Name" required
                     id="outlined-required"
                     label="name"
                     defaultValue={name}
                     onChange={(e)=>setNames(e.target.value)}
                     />  
                  <input placeholder="Description" required
                  id="outlined-required"
                  label="Description"
                  defaultValue={description}
                  onChange={(e)=>setDescriptions(e.target.value)}
                  />
                  <input placeholder="Categories" required
                  id="outlined-required"
                  label="Categories"
                  defaultValue={categories}
                  onChange={(e)=>setCategories(e.target.value)}
                  />  
                  <input placeholder="Image Url" required
                  id="outlined-required"
                  label="Image Url"
                  style={{backgroundColor:"white"}}
                  defaultValue={imageUrl}
                  onChange={(e)=>setImageUrls(e.target.value)}
                  />  
              </div>
          </div>
      </div>
<button>
Submit
</button>
  </div>
  
</form>
)} }

export default Update
